if (!function_exists('is_logged_in')) {

    function is_logged_in() {
        $CI = & get_instance();
        if ($CI->session->userdata('users_info') == NULL) {
            return FALSE;
        } else {
            return TRUE;
        }
    }

}
if (!function_exists('view_uploaded_image')) {

    function view_uploaded_image($id) {
        $CI = & get_instance();
        $noimage = 'data:image/png;base64,';

        $sql = $CI->db->where('id', $id)->get('file_details');
        if ($sql->num_rows() >= 1) {
            $file = $sql->row_array();
            switch (strtolower($file['file_ext'])) {
                case 'png':
                    $type = 'image/png';
                    break;
                case 'jpg':
                    $type = 'image/jpg';
                    break;
                case 'gif':
                    $type = 'image/gif';
                    break;
                default:
                    $type = false;
            }
            if ($type) {
                $data = file_get_contents(APPPATH . 'file_share/' . $file['file_name']);
                return "data:$type;base64," . base64_encode($data);
            } else {
                return $noimage;
            }
        } else {
            return $noimage;
        }
    }

}
