if (!function_exists('my_download')) {

    function my_download($filename = '', $file = '') {
        if ($filename == '' OR $file == '') {
            return FALSE;
        }

        // Try to determine if the filename includes a file extension.
        // We need it in order to set the MIME type
        if (FALSE === strpos($filename, '.')) {
            return FALSE;
        }

        // Grab the file extension
        $x = explode('.', $filename);
        $extension = end($x);

        // Load the mime types
        @include(APPPATH . 'config/mimes' . EXT);

        // Set a default mime if we can't find it
        if (!isset($mimes[$extension])) {
            $mime = 'application/octet-stream';
        } else {
            $mime = (is_array($mimes[$extension])) ? $mimes[$extension][0] : $mimes[$extension];
        }

        // Generate the server headers
        if (strpos($_SERVER['HTTP_USER_AGENT'], "MSIE") !== FALSE) {
            header('Content-Type: "' . $mime . '"');
            header('Content-Disposition: attachment; filename="' . $filename . '"');
            header('Expires: 0');
            header('Cache-Control: must-revalidate, post-check=0, pre-check=0');
            header("Content-Transfer-Encoding: binary");
            header('Pragma: public');
            header("Content-Length: " . filesize($file));
        } else {
            header('Content-Type: "' . $mime . '"');
            header('Content-Disposition: attachment; filename="' . $filename . '"');
            header("Content-Transfer-Encoding: binary");
            header('Expires: 0');
            header('Pragma: no-cache');
            header("Content-Length: " . filesize($file));
        }

        readfile_chunked($file);
        die;
    }

}
if (!function_exists('readfile_chunked')) {

    function readfile_chunked($file, $retbytes = TRUE) {
        $chunksize = 1 * (1024 * 1024);
        $buffer = '';
        $cnt = 0;

        $handle = fopen($file, 'r');
        if ($handle === FALSE) {
            return FALSE;
        }

        while (!feof($handle)) {
            $buffer = fread($handle, $chunksize);
            echo $buffer;
            ob_flush();
            flush();

            if ($retbytes) {
                $cnt += strlen($buffer);
            }
        }

        $status = fclose($handle);

        if ($retbytes AND $status) {
            return $cnt;
        }

        return $status;
    }

}
if (!function_exists('get_queued_attach')) {

    function get_queued_attach($attach_table) {
        $CI = & get_instance();
        $users_id = $CI->session->userdata('users_info')['id'];
        $data = [];
        $sql = $CI->db->select('file_details_id')->where('attach_table', $attach_table)->where('users_id', $users_id)->where('status', 'queued')->get('temp_file_upload');
        if ($sql->num_rows() > 0) {
            $data = $sql->result_array();
        }
        return $data;
    }

}
