}
if(!function_exists('notifications')){
    function notifications(){
        $CI =& get_instance();
        $sql = $CI->db->where('to_users_id', $CI->session->userdata('users_info')['id'])->where('status', 'unread')->limit(10)->get('notification');
        $html = '';
        if($sql->num_rows() > 0){
            foreach ($sql->result() as $noti){
                if($noti->table == 'projects'){
                    $link = base_url('projects/details/'.$noti->table_id);
                }else{
                    $link = base_url('tasks/details/'.$noti->table_id);
                }
                $html .= '<li><a rel="nofollow" href="javascript:notification('.$noti->id.', \''.$link.'\')" class="dropdown-item">'.$noti->notice.'</a></li>';
            }
        }else{
            $html = '<li><a rel="nofollow" href="#" class="dropdown-item">No Notification</a></li>';
        }
        return $html;
    }
}